function getEl(e){return"string"==typeof e?document.querySelector(e):e}function prepareText(){var e=getEl(".quoteText"),t=getEl(".quoteAuthor"),o=famousQuotes.getRandom();e.innerHTML=o.text,t.innerHTML=o.author}function adjustScreen(){var e=getEl(".blockquote"),t=getEl(".quoteText"),o=getEl(".quoteAuthor"),n=convertFontToEm(t),l=convertFontToEm(o),r=l/n.toFixed(3);for(console.log("clientHeight: "+e.clientHeight+", scrollHeight: "+e.scrollHeight);e.clientHeight<e.scrollHeight;)n-=.1,t.style.fontSize=n+"em",o.style.fontSize=n*r+"em"}function convertFontToEm(e){var t=parseInt(window.getComputedStyle(getEl(e),null).fontSize),o=parseInt(window.getComputedStyle(getEl("body"),null).fontSize),n=t/o;return console.log(t/o+"in em"),t/o}prepareText(),adjustScreen();